body.script:
// Replace with your m3u8 URL:
const streamUrl = "https://allottplay.com/tv/toffee/live.php?id=cartoon_network_sd&e=.m3u8";

const video = document.getElementById('video');
const playBtn = document.getElementById('playBtn');
const muteBtn = document.getElementById('muteBtn');

// Play button handler (helps with autoplay policies)
playBtn.addEventListener('click', () => {
  video.play().catch(e => console.log('Play failed:', e));
});
muteBtn.addEventListener('click', () => {
  video.muted = !video.muted;
});

// If browser (Safari/iOS) supports HLS natively, set src directly
if (video.canPlayType('application/vnd.apple.mpegurl')) {
  video.src = streamUrl;
  // optional: load immediately
  video.addEventListener('loadedmetadata', () => {
    console.log('Native HLS ready');
  });
} else if (Hls.isSupported()) {
  const hls = new Hls({
    // optional config (debug:false etc)
  });
  hls.loadSource(streamUrl);
  hls.attachMedia(video);
  hls.on(Hls.Events.MANIFEST_PARSED, function() {
    console.log('HLS manifest parsed, ready to play');
    // don't autoplay forcibly; use the Play button to satisfy autoplay policies
  });
  hls.on(Hls.Events.ERROR, function(event, data) {
    console.error('hls.js error', event, data);
  });
} else {
  // Very old browser
  console.error('HLS not supported in this browser');
  alert('Aapka browser HLS stream nahi chala paa raha. Chrome/Firefox/Edge (latest) try karein ya Safari par dekhein.');
}
